import React, { Component } from "react";
import styled from "styled-components";


class ComposeButton extends Component {
  constructor(props) {
      super(props);
      this.state = {
        ComposeButton: "border: 3px solid rgba(247,63,0,0.60);\
            border-radius: 8px;\
            height: 45px;\
            display: flex;\
          align-items: center;\
          justify-content:center;\
          margin-top: -109px;",
          Compose_email_button_text: "\
          font-size: 20px;\
          font-weight: 400;\
          font-style: normal;\
          width: 100%;\
          height: 100%;\
          line-height: -1px;\
          letter-spacing: 3px;\
          text-align: center;\
          color: rgba(247,63,0,0.60);\
          margin: 0;\
          margin-left: 30px;\
          margin-right: 30px;"
  }
}

  handleHoverCompose(status) {
    if (status == false ) {
    this.setState({ComposeButton:"border: 3px solid rgba(247,63,0,0.60);\
        border-radius: 8px;\
        height: 45px;\
        display: flex;\
      align-items: center;\
      justify-content:center;\
      margin-top: -109px;"})
    this.setState({Compose_email_button_text: "\
              font-size: 20px;\
              font-weight: 400;\
              font-style: normal;\
              width: 100%;\
              height: 100%;\
              line-height: -1px;\
              letter-spacing: 3px;\
              text-align: center;\
              color: rgba(247,63,0,0.60);\
              margin: 0;\
              margin-left: 30px;\
              margin-right: 30px;"
    })
    } else {
      this.setState({ComposeButton: "border: 0px solid rgba(247,63,0,0.60);\
      border-radius: 8px;\
      height: 45px;\
      display: flex;\
      align-items: center;\
      justify-content:center;\
      margin-top: -109px;\
      background: rgba(245,118,35,0.71);\
      border-radius: 8px;"})
      this.setState({Compose_email_button_text: "\
                font-size: 20px;\
                font-weight: 400;\
                font-style: normal;\
                width: 100%;\
                height: 100%;\
                line-height: -1px;\
                letter-spacing: 3px;\
                text-align: center;\
                color: #FFFFFF;\
                margin: 0;\
                margin-left: 30px;\
                margin-right: 30px;"
      })
    }


  }

  render() {
    const Compose_email_button = styled.button`
${this.state.ComposeButton};
}
    `

    const Compose_email_button_text = styled.p`
    ${this.state.Compose_email_button_text};
        `


    return (
      //  <a href='mailto:?bcc=Undisclosed Recipients<taher.el.sheikh@gmail.com> \
      // // &body=The following is everything I have to say about the color blue.'>Email Us</a>

        <Compose_email_button onMouseEnter={()=>this.handleHoverCompose(true)}
        onMouseLeave={()=>this.handleHoverCompose(false)}
        onClick={()=>{console.log(this.props.state.emails)}}
        >
          <Compose_email_button_text>compose email
          </Compose_email_button_text>
        </Compose_email_button>
    );

  }
}

export default ComposeButton;
