import React, { Component } from "react";
import styled from "styled-components";


class InputTesting extends Component {
  constructor() {
      super();
      this.state = {
        firstName: "",
        secondName: "",
        domainName: ""
}
this.handleFirstName = this.handleFirstName.bind(this)
this.handleSecondName = this.handleSecondName.bind(this)
this.handleDomainName = this.handleDomainName.bind(this)

}

handleFirstName(event) {
let firstName = event.target.value
this.setState({firstName: firstName}, ()=>{this.props.passFirstName(this.state.firstName)} )
}

handleSecondName(event) {
this.setState({ secondName: event.target.value})
}

handleDomainName(event) {
this.setState({ domainName: event.target.value})
}



render() {
  const Input = styled.input`
            width: 229px;
            height: 42px;
            background: transparent;
            border: solid 1px rgba(0, 0, 0, 0.45);
            box-shadow: 0px 0px 0px 1.5px #f8e71c;
            border-radius: 8px;
            display: block;

            font-size: 15px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            padding-left: 5px;

          `;

  const Label = styled.label`
            height: 24px;
            font-size: 17px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            display: block;
          `;



    return (

      <div id="Flexbox_Div_inputs">
        <div>
          <Label>FIRST NAME</Label>
          <input id="MainInput" value={this.state.firstName} onChange={(event)=>{this.handleFirstName(event)}} placeholder="mark"/>
        </div>
        <div>
          <Label>SECOND NAME</Label>
          <input id="MainInput" value={this.state.secondName} onChange={(event)=>{this.handleSecondName(event); this.props.passingData(event);}}   placeholder="zuck"/>
        </div>
        <div>
          <Label>DOMAIN</Label>
          <input id="MainInput" value={this.state.domainName} onChange={(event)=>{this.handleDomainName(event); this.props.passingData(event);}}  placeholder="@fb.com"/>
        </div>
        </div>
    );
  }
}


export default InputTesting;
