import React, { Component } from "react";
import styled from "styled-components";


class InputTesting extends Component {
  constructor() {
      super();
      this.state = {
        firstName: "",
        secondName: "",
        domainName: "",
        emailData: "",
        errorFirstNameMessage: "",
        errorFirstNameFound: "",
        errorSecondNameMessage: "",
        errorSecondNameFound: "",
        errorDomainNameMessage: "",
        errorDomainNameFound: ""
}
this.handleFirstName = this.handleFirstName.bind(this)
this.handleSecondName = this.handleSecondName.bind(this)
this.handleDomainName = this.handleDomainName.bind(this)
this.stringManipulation = this.stringManipulation.bind(this)
this.removeWhiteSpace = this.removeWhiteSpace.bind(this)
this.checkWhitespace = this.checkWhitespace.bind(this)
this.checkEmptyInputs = this.checkEmptyInputs.bind(this)
this.checkDomain = this.checkDomain.bind(this)
}

handleFirstName(event) {
this.setState({firstName: event.target.value})
}


handleSecondName(event) {
this.setState({ secondName: event.target.value})
}

handleDomainName(event) {
this.setState({ domainName: event.target.value})
}

passData() {
  // return (this.state.firstName, this.state.secondName, this.state.domainName)
  let data = { first_name: this.state.firstName, second_name: this.state.secondName, domain_name: this.state.domainName }
  return ( data )
}

// check if there is any whitespace
checkWhitespace() {
  if ( /\s/.test(this.state.firstName)==true) {
    console.log("ERROR space firstname")
    this.setState({errorFirstNameFound: true})
    this.setState({errorFirstNameMessage: "you cant have whitespace"})
  }
  else if ( /\s/.test(this.state.secondName)==true) {
    console.log("ERROR space secondName")
    this.setState({errorSecondNameFound: true})
    this.setState({errorSecondNameMessage: "you cant have whitespace"})

  }  else if ( /\s/.test(this.state.domainName)==true) {
      console.log("ERROR space domainName")
      this.setState({errorThirdNameFound: true})
      this.setState({errorThirdNameMessage: "you cant have whitespace"})
    }

    else {
      this.setState({errorFirstNameFound: false})
      this.setState({errorSecondNameFound: false})
      this.setState({errorThirdNameFound: false})
      this.setState({errorFirstNameMessage: ""})
      this.setState({errorSecondNameMessage: ""})
      this.setState({errorThirdNameMessage: ""})
      this.checkEmptyInputs()
    }

}

// check empty inputs
checkEmptyInputs() {
  if ( this.state.firstName==null || this.state.firstName=="") {
    console.log("ERROR empty inputs firstName")
    this.setState({errorFirstNameFound: true})
    this.setState({errorFirstNameMessage: "whats the first name?"})
  }
  else if ( this.state.secondName==null || this.state.secondName==""){
    console.log("ERROR empty inputs secondName")
    this.setState({errorFirstNameFound: true})
    this.setState({errorFirstNameMessage: "whats the second name?"})

  }  else if ( this.state.domainName==null || this.state.domainName=="") {
      console.log("ERROR empty inputs domainName")
      this.setState({errorFirstNameFound: true})
      this.setState({errorFirstNameMessage: "whats the domain name?"})
    }

    else {
      this.setState({errorFirstNameFound: false})
      this.setState({errorSecondNameFound: false})
      this.setState({errorThirdNameFound: false})
      this.setState({errorFirstNameMessage: ""})
      this.setState({errorSecondNameMessage: ""})
      this.setState({errorThirdNameMessage: ""})
      this.checkDomain()

    }
}

checkDomain() {
  let domainname = this.state.domainName
     if (domainname.charAt(0) !== "@" || domainname.includes(".") === false ) {
       console.log("ERROR @ or . ")
     } else {
       console.log("GOOD @ or .")
     }


}

checkLength() {

}

sanityChecking() {
  // let firstname = this.state.firstName
  // let secondname = this.state.secondName
  // let domainname = this.state.domainName

  this.checkWhitespace()

  // // check if there is any whitespace
  // if ( /\s/.test(firstname)==true ||  /\s/.test(secondname)==true ||  /\s/.test(domainname)==true ) {
  //   console.log("ERROR space")
  //   this.setState({errorFirstNameFound: true})
  //   this.setState({errorFirstNameMessage: "you cant have whitespace"})
  //
  // } else {
  //   console.log("GOOD space")
//
//
//   }
//
//   // check that all inputs are not empty
//   if (firstname==null || firstname=="" || secondname==null || secondname=="" || domainname==null || domainname=="")
// {
//   console.log("ERROR empty string ")
// } else {
//   console.log("GOOD empty string")
// }
//
//
//    // that domain has @ in it and at least one dot
//    if (domainname.charAt(0) !== "@" || domainname.includes(".") === false ) {
//      console.log("ERROR @ or . ")
//    } else {
//      console.log("GOOD @ or .")
//    }
//
//
//    // check length of inputs
//    if (firstname.length > 15 || secondname.length > 15 || domainname.length > 24) {
//        console.log("ERROR length ")
//       } else {
//         console.log("GOOD length")
//       }

      this.predictEmail()
}

removeWhiteSpace() {
  this.setState({ firstName: this.state.firstName.trim() },
  this.setState({ secondName: this.state.secondName.trim()},
  this.setState({ domainName: this.state.domainName.trim()},
    ()=> {this.sanityChecking()} )
    )
  )
  // this.setState({ secondName: this.state.secondName.trim() })
  // this.setState({ domainName: this.state.domainName.trim() })
}

predictEmail() {
  let firstname = this.state.firstName
  let secondname = this.state.secondName
  let domainname = this.state.domainName
  let emailOne = firstname+domainname
  let emailTwo = secondname+domainname
  let emailThree = firstname+secondname+domainname
  let emailFour = firstname.charAt(0)+secondname+domainname
  let emailFive = firstname+"."+secondname+domainname
  let emailSix = firstname+"_"+secondname+domainname

  let emailData = {
    emailOne: emailOne, emailTwo: emailTwo, emailThree: emailThree,
    emailFour: emailFour, emailFive: emailFive, emailSix: emailSix
  }

  // this.props.passDataApp(emailData)
  // console.log(emailData)
  this.setState({emailData: emailData},   ()=> {this.props.passDataApp(this.state.emailData)}  );

}

stringManipulation() {
  this.removeWhiteSpace()
}

errorInputs() {

  const Label = styled.label`
            height: 24px;
            font-size: 17px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            display: block;
          `;

    const Star = styled.label`
                    color: #FD0909;
                  `;




    const ErrorFont = styled.p`
    color: #FD0909;
    display: block;
    margin-top : 5.5px;
    `



  return(
    <div>
    <Label><Star>*</Star> FIRST NAME</Label>
    <input id="ErrorInput" value={this.state.firstName} onChange={(event)=>{this.handleFirstName(event);}} placeholder="mark"/>
    <ErrorFont>hmm this is required </ErrorFont>
    </div>
  )
}


render() {
  const Input = styled.input`
            width: 229px;
            height: 42px;
            background: transparent;
            border: solid 1px rgba(0, 0, 0, 0.45);
            box-shadow: 0px 0px 0px 1.5px #f8e71c;
            border-radius: 8px;
            display: block;
            font-size: 15px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            padding-left: 5px;

          `;

  const Label = styled.label`
            height: 24px;
            font-size: 17px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            display: block;
          `;



    return (

      <div id="Flexbox_Div_inputs">
        <div>
          {this.errorInputs()}
        </div>
        <div>
          <Label>SECOND NAME</Label>
          <input id="MainInput" value={this.state.secondName} onChange={(event)=>{this.handleSecondName(event);}}   placeholder="zuck"/>
        </div>
        <div>
          <Label>DOMAIN</Label>
          <input id="MainInput" value={this.state.domainName} onChange={(event)=>{this.handleDomainName(event);}}  placeholder="@fb.com"/>
        </div>
        </div>
    );
  }
}


export default InputTesting;
