import React, { Component } from "react";
import styled from "styled-components";


class InputTesting extends Component {
  constructor() {
      super();
      this.state = {
        firstName: "",
        secondName: "",
        domainName: "",
        emailData: ""
}
this.handleFirstName = this.handleFirstName.bind(this)
this.handleSecondName = this.handleSecondName.bind(this)
this.handleDomainName = this.handleDomainName.bind(this)
this.stringManipulation = this.stringManipulation.bind(this)

}

handleFirstName(event) {
this.setState({firstName: event.target.value})
}


handleSecondName(event) {
this.setState({ secondName: event.target.value})
}

handleDomainName(event) {
this.setState({ domainName: event.target.value})
}

passData() {
  // return (this.state.firstName, this.state.secondName, this.state.domainName)
  let data = { first_name: this.state.firstName, second_name: this.state.secondName, domain_name: this.state.domainName }
  return ( data )
}

sanityChecking() {
  // check that all inputs are not empty
  let firstname = this.state.firstName
  let secondname = this.state.secondName
  let domainname = this.state.domainName

  if (firstname==null || firstname=="" || secondname==null || secondname=="" || domainname==null || domainname=="")
{
  console.log("ERROR empty string ")
} else {
  console.log("good to go empty string")
}




   // that domain has @ in it and at least one dot
   if (domainname.charAt(0) !== "@" || domainname.includes(".") ) {
     console.log("ERROR there is no @ || . ")
   } else {
     console.log("good to go @")
   }

}

stringManipulation() {
  this.sanityChecking()
  let firstname = this.state.firstName
  let secondname = this.state.secondName
  let domainname = this.state.domainName
  let emailOne = firstname+domainname
  let emailTwo = secondname+domainname
  let emailThree = firstname+secondname+domainname
  let emailFour = firstname.charAt(0)+secondname+domainname
  let emailFive = firstname+"."+secondname+domainname
  let emailSix = firstname+"_"+secondname+domainname

  let emailData = {
    emailOne: emailOne, emailTwo: emailTwo, emailThree: emailThree,
    emailFour: emailFour, emailFive: emailFive, emailSix: emailSix
  }

  // this.props.passDataApp(emailData)
  // console.log(emailData)
  this.setState({emailData: emailData},   ()=> {this.props.passDataApp(this.state.emailData)}  );
  // this.setState({emailData: emailData}, this.props.passDataApp(this.state.emailData))

}

render() {
  const Input = styled.input`
            width: 229px;
            height: 42px;
            background: transparent;
            border: solid 1px rgba(0, 0, 0, 0.45);
            box-shadow: 0px 0px 0px 1.5px #f8e71c;
            border-radius: 8px;
            display: block;
            font-size: 15px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            padding-left: 5px;

          `;

  const Label = styled.label`
            height: 24px;
            font-size: 17px;
            font-weight: normal;
            font-style: normal;
            font-stretch: normal;
            line-height: normal;
            letter-spacing: 0.7px;
            color: #ffffff;
            display: block;
          `;



    return (

      <div id="Flexbox_Div_inputs">
        <div>
          <Label>FIRST NAME</Label>
          <input id="MainInput" value={this.state.firstName} onChange={(event)=>{this.handleFirstName(event);}} placeholder="mark"/>
        </div>
        <div>
          <Label>SECOND NAME</Label>
          <input id="MainInput" value={this.state.secondName} onChange={(event)=>{this.handleSecondName(event);}}   placeholder="zuck"/>
        </div>
        <div>
          <Label>DOMAIN</Label>
          <input id="MainInput" value={this.state.domainName} onChange={(event)=>{this.handleDomainName(event);}}  placeholder="@fb.com"/>
        </div>
        </div>
    );
  }
}


export default InputTesting;
